import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [playerPos, setPlayerPos] = useState(1);
  const [diceResult, setDiceResult] = useState(null);
  const [tiles, setTiles] = useState([]);

  const ladders = {
    4: 14, 9: 31, 20: 38, 28: 84,
    40: 59, 51: 67, 63: 81, 71: 91
  };

  const snakes = {
    17: 7, 54: 34, 62: 19, 64: 60,
    87: 24, 93: 73, 95: 75, 99: 78
  };

  useEffect(() => {
    const newTiles = [];
    let direction = 1;
    for (let row = 9; row >= 0; row--) {
      for (let col = 0; col < 10; col++) {
        const number = row * 10 + (direction === 1 ? col + 1 : 10 - col);
        newTiles.push(number);
      }
      direction *= -1;
    }
    setTiles(newTiles);
  }, []);

  const rollDice = () => {
    const result = Math.floor(Math.random() * 6) + 1;
    setDiceResult(result);

    let newPos = playerPos + result;
    if (newPos > 100) newPos = playerPos;

    if (ladders[newPos]) {
      newPos = ladders[newPos];
    } else if (snakes[newPos]) {
      newPos = snakes[newPos];
    }

    setTimeout(() => setPlayerPos(newPos), 500);
  };

  return (
    <div className="game">
      <h1>Snakes and Ladders</h1>
      <div className="controls">
        <button className="button" onClick={rollDice}>Roll Dice</button>
        <div id="dice-results">{diceResult !== null ? `You rolled: ${diceResult}` : ""}</div>
      </div>
      <div id="board" style={{ backgroundImage: "url('/img/SnakeLadder.png')" }}>
        {tiles.map((num, index) => (
          <div className="tile" key={index}>
            {num}
            {playerPos === num && <div className="player" style={{ backgroundColor: "blue" }}>P</div>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
