import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [players, setPlayers] = useState([]);
  const [numPlayers, setNumPlayers] = useState(2);
  const [currentPlayer, setCurrentPlayer] = useState(0);
  const [diceResult, setDiceResult] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);
  const [tiles, setTiles] = useState([]);

  const ladders = {
    4: 14, 9: 31, 20: 38, 28: 84,
    40: 59, 51: 67, 63: 81, 71: 91
  };

  const snakes = {
    17: 7, 54: 34, 62: 19, 64: 60,
    87: 24, 93: 73, 95: 75, 99: 78
  };

  useEffect(() => {
    // Generate tiles from 1 to 100 from top-left to bottom-right (1 at top-left)
    const newTiles = [];
    for (let row = 0; row < 10; row++) {
      const direction = row % 2 === 0 ? 1 : -1;
      for (let col = 0; col < 10; col++) {
        const base = row * 10;
        const number = direction === 1 ? base + col + 1 : base + (9 - col + 1);
        newTiles.push(number);
      }
    }
    setTiles(newTiles);
  }, []);

  const startGame = () => {
    const initPlayers = [];
    for (let i = 0; i < numPlayers; i++) {
      initPlayers.push({ id: i, pos: 1 });
    }
    setPlayers(initPlayers);
    setCurrentPlayer(0);
    setGameStarted(true);
  };

  const rollDice = () => {
    const result = Math.floor(Math.random() * 6) + 1;
    setDiceResult(result);

    const updatedPlayers = [...players];
    let newPos = updatedPlayers[currentPlayer].pos + result;
    if (newPos > 100) newPos = updatedPlayers[currentPlayer].pos;

    if (ladders[newPos]) newPos = ladders[newPos];
    else if (snakes[newPos]) newPos = snakes[newPos];

    updatedPlayers[currentPlayer].pos = newPos;
    setPlayers(updatedPlayers);

    if (newPos === 100) {
      alert(`ðŸŽ‰ Player ${currentPlayer + 1} wins!`);
      setGameStarted(false);
    } else {
      setCurrentPlayer((currentPlayer + 1) % numPlayers);
    }
  };

  const playerColors = ['blue', 'red', 'green', 'purple'];

  return (
    <div
      className="game"
      style={{
        backgroundImage: "url('/img/art.png')",
        backgroundSize: "cover",
        backgroundAttachment: "fixed",
        backgroundPosition: "center"
      }}
    >
      <h1>Snakes and Ladders</h1>

      {!gameStarted ? (
        <div className="setup">
          <label>Select number of players:</label>
          <select value={numPlayers} onChange={(e) => setNumPlayers(Number(e.target.value))}>
            {[2, 3, 4].map(n => <option key={n} value={n}>{n}</option>)}
          </select>
          <button className="button" onClick={startGame}>Start Game</button>
        </div>
      ) : (
        <>
          <div className="controls">
            <p>ðŸŽ² Player {currentPlayer + 1}'s turn</p>
            <button className="button" onClick={rollDice}>Roll Dice</button>
            <div id="dice-results">{diceResult !== null ? `You rolled: ${diceResult}` : ""}</div>
          </div>
        </>
      )}

      <div
        id="board"
        style={{
          backgroundImage: "url('/img/SnakeLadder.png')",
          backgroundRepeat: "no-repeat",
          backgroundSize: "contain"
        }}
      >
        {tiles.map((num, index) => (
          <div className="tile" key={index}>
            {/* No number overlay */}
            {players.map((p, i) =>
              p.pos === num && (
                <div
                  key={i}
                  className="player"
                  style={{
                    backgroundColor: playerColors[p.id],
                    left: `${i * 18}px`
                  }}
                >
                  {p.id + 1}
                </div>
              )
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
